const FlightDetails = () => {
  const { eventId } = useParams();
  const { state: navState } = useLocation();
  const {
    origin,
    destinationAirports = [],  // <-- use this array for dropdown
    departureDate,
    eventState,
    daysBefore = 0,
    daysAfter = 0,
  } = navState || {};

  const [flights, setFlights] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Filters
  const [maxStops, setMaxStops] = useState("");
  const [selectedAirport, setSelectedAirport] = useState("");
  const [sortBy, setSortBy] = useState("");

  const formatFlight = (flight) => {
    const itinerary = flight.itineraries?.[0];
    if (!itinerary || !itinerary.segments?.length) return null;

    const segments = itinerary.segments;
    const totalDurationMinutes = segments.reduce((sum, seg) => {
      const match = seg.duration.match(/PT(?:(\d+)H)?(?:(\d+)M)?/);
      if (!match) return sum;
      const hours = parseInt(match[1] || 0);
      const minutes = parseInt(match[2] || 0);
      return sum + hours * 60 + minutes;
    }, 0);

    const airlineCodes = [...new Set(segments.map((seg) => seg.carrierCode))];

    return {
      airlines: airlineCodes.join(", "),
      price: parseFloat(flight.price?.total || 0),
      currency: flight.price?.currency || "USD",
      departure: segments[0].departure.iataCode,
      arrival: segments[segments.length - 1].arrival.iataCode,
      duration: `${Math.floor(totalDurationMinutes / 60)}h ${totalDurationMinutes % 60}m`,
      durationMinutes: totalDurationMinutes,
      stops: segments.length - 1,
    };
  };

  // Fetch flights when user selects an airport
  useEffect(() => {
    if (!origin || !selectedAirport || !departureDate) return;

    const fetchFlights = async () => {
      setLoading(true);
      setError(null);

      try {
        const BACKEND_URL = process.env.REACT_APP_BACKEND_URL || "https://vibent-api.onrender.com";

        const startDate = new Date(departureDate);
        startDate.setDate(startDate.getDate() - daysBefore);
        const endDate = new Date(departureDate);
        endDate.setDate(endDate.getDate() + daysAfter);
        const formatDate = (d) => d.toISOString().split("T")[0];

        const url = `${BACKEND_URL}/flights?origin=${origin}&destination=${selectedAirport}&departureDate=${formatDate(
          startDate
        )}&returnDate=${formatDate(endDate)}`;

        const res = await fetch(url);
        if (!res.ok) {
          const text = await res.text();
          throw new Error(`Flights API returned ${res.status}: ${text}`);
        }

        const json = await res.json();
        setFlights(json.data || []);
      } catch (err) {
        setError(err.message);
        setFlights([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFlights();
  }, [origin, selectedAirport, departureDate, daysBefore, daysAfter]);

  // Apply filters
  let filteredFlights = flights
    .map(formatFlight)
    .filter((f) => f && (!maxStops || f.stops <= parseInt(maxStops)));

  // Apply sorting
  if (sortBy) {
    filteredFlights.sort((a, b) => {
      switch (sortBy) {
        case "priceLowHigh":
          return a.price - b.price;
        case "priceHighLow":
          return b.price - a.price;
        case "duration":
          return a.durationMinutes - b.durationMinutes;
        case "stops":
          return a.stops - b.stops;
        default:
          return 0;
      }
    });
  }

  return (
    <div className="top-artists-container">
      <h2 className="title">Flights for Event {eventId}</h2>

      {/* Filters + Sort */}
      <div style={{ display: "flex", gap: "16px", marginBottom: "20px", flexWrap: "wrap", background: "#1e1e1e", padding: "12px", borderRadius: "12px" }}>
        <input
          type="number"
          placeholder="Max Stops"
          value={maxStops}
          onChange={(e) => setMaxStops(e.target.value)}
          style={{ padding: "8px", borderRadius: "8px" }}
        />

        {destinationAirports.length > 0 && (
          <select
            value={selectedAirport}
            onChange={(e) => setSelectedAirport(e.target.value)}
            style={{ padding: "8px", borderRadius: "8px" }}
          >
            <option value="">Select Airport</option>
            {destinationAirports.map((code) => (
              <option key={code} value={code}>{code}</option>
            ))}
          </select>
        )}

        <select
          value={sortBy}
          onChange={(e) => setSortBy(e.target.value)}
          style={{ padding: "8px", borderRadius: "8px" }}
        >
          <option value="">Sort By</option>
          <option value="priceLowHigh">Price: Low to High</option>
          <option value="priceHighLow">Price: High to Low</option>
          <option value="duration">Duration: Shortest</option>
          <option value="stops">Stops: Fewest</option>
        </select>
      </div>

      {loading && <p>Loading flights...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && !error && filteredFlights.length === 0 && <p>No flights found.</p>}

      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(400px, 1fr))", gap: "24px", width: "100%" }}>
        {filteredFlights.map((f, idx) => (
          <div key={idx} style={{ background: "#1e1e1e", borderRadius: "16px", padding: "20px", boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)", color: "#fff", display: "flex", justifyContent: "space-between", alignItems: "flex-start" }}>
            <div>
              <p><strong>Airlines:</strong> {f.airlines}</p>
              <p><strong>Departure:</strong> {formatWithTimezone(flight.itineraries[0].segments[0].departure.at, eventState)}</p>
              <p><strong>Arrival:</strong> {formatWithTimezone(flight.itineraries[0].segments.slice(-1)[0].arrival.at, eventState)}</p>
            </div>
            <div style={{ textAlign: "right" }}>
              <p style={{ fontSize: "1.2rem", fontWeight: "bold", color: "#1DB954" }}>${f.price} {f.currency}</p>
              <p style={{ color: "#aaa", fontSize: "0.9rem" }}>Duration: {f.duration}</p>
              <p style={{ color: "#aaa", fontSize: "0.9rem" }}>Stops: {f.stops}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FlightDetails;
